args:
echo '
#message-log {
    position: absolute;
    bottom: 20px;
    right: 20px;
    width: 400px;
    max-height: 250px;
    overflow-y: hidden;
    background: linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%);
    padding: 10px;
    color: #eee;
    font-size: 14px;
    z-index: 100;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    pointer-events: none;
}

.log-entry {
    margin-bottom: 5px;
    text-shadow: 1px 1px 2px #000;
    opacity: 0;
    animation: fadeIn 0.3s forwards;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

.log-entry.combat { color: #ff6666; }
.log-entry.player-hit { color: #ff3333; font-weight: bold; }
.log-entry.monster-die { color: #ffaa00; font-weight: bold; }
.log-entry.item { color: #ffff00; }
.log-entry.torch { color: #ffcc00; }
.log-entry.door { color: #aaaaff; }
' >> style.css

cat >> style.css <<EOF

#intro-screen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    z-index: 300;
    color: #e0e0e0;
    text-align: center;
    padding: 40px;
}

#intro-screen h1 {
    font-size: 48px;
    color: #ffaa00;
    margin-bottom: 30px;
    text-shadow: 0 0 10px rgba(255, 170, 0, 0.5);
    font-family: 'Courier New', monospace;
    letter-spacing: 2px;
}

#intro-screen p {
    font-size: 18px;
    max-width: 800px;
    margin-bottom: 20px;
    line-height: 1.6;
    color: #ccc;
}

#intro-screen .highlight {
    color: #ff4444;
    font-weight: bold;
    font-size: 22px;
    margin-top: 20px;
    text-shadow: 0 0 5px rgba(255, 68, 68, 0.3);
}

#intro-screen .press-key {
    margin-top: 50px;
    font-size: 20px;
    color: #fff;
    animation: pulse 1.5s infinite;
}

@keyframes pulse {
    0% { opacity: 0.5; }
    50% { opacity: 1; }
    100% { opacity: 0.5; }
}
EOF

cat >> style.css <<EOF

